import React, { Component } from 'react';
import mockData from './mockData'; // Import mock data

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

class SearchNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      search: props.search,
    };
  }

  render() {
    const { articles } = this.state;
    const { classes } = this.props;

    if (this.state.search !== this.props.search) this.reloadSearch();
    return (
      <div className={classes.root}>
        <GridList cellHeight={180} className={classes.gridList}>
          <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
            <ListSubheader component="div">Mencari berita {this.props.search}</ListSubheader>
          </GridListTile>
          {articles.map((article) => (
            <GridListTile key={article.urlToImage}>
              <img src={article.urlToImage} alt={article.title} />
              <GridListTileBar
                title={article.title}
                subtitle={<span>by: {article.source.name}</span>}
                actionIcon={
                  <IconButton className={classes.icon} href={article.url}>
                    <InfoIcon />
                  </IconButton>
                }
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }

  componentDidMount() {
    // Use mock data instead of making an API call
    this.setState({
      articles: mockData.results,
    });
  }

  reloadSearch() {
    this.setState({
      articles: mockData.results,
    });
  }
}

const styles = (theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 1100,
    height: 900,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
});

SearchNews.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SearchNews);
