import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      source: props.source,
    };
  }

  render() {
    let { articles, source } = this.state;

    if (source !== this.props.source) this.reloadSource();
    return (
      <div className="container">
        <h2>News from {this.props.source}</h2>
        <div className="grid-list">
          {articles.map((article) => (
            <div className="grid-item" key={article.url}>
              <img src={article.urlToImage} alt={article.title} />
              <div className="grid-item-bar">
                <h3>{article.title}</h3>
                <p>by: {article.source.name}</p>
                <a href={article.url} className="btn" target="_blank" rel="noopener noreferrer">
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.reloadSource();
  }

  reloadSource() {
    axios
      .get(`${URL.topHeadlinesURL}${URL.apiKey}${URL.category}${this.props.source}`)
      .then((response) => {
        const { data } = response;
        this.setState({
          articles: data.results,
        });
      })
      .catch((error) => {
        console.error('Error fetching news:', error);
      });
  }
}

export default News;
