import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

class Headlines extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
    };
  }

  render() {
    const { articles } = this.state;

    return (
      <div className="container">
        <h2>Top Headlines</h2>
        <div className="grid-list">
          {/* Featured Headline */}
          {articles[0] && (
            <div className="grid-item featured">
              <img src={articles[0].image_url} alt={articles[0].title} />
              <div className="grid-item-bar">
                <h2>{articles[0].title}</h2>
                <p>by: {articles[0].source_id}</p>
                <a href={articles[0].link} className="btn" target="_blank" rel="noopener noreferrer">
                  Read More
                </a>
              </div>
            </div>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1).map((article) => (
            <div className="grid-item" key={article.link}>
              <img src={article.image_url} alt={article.title} />
              <div className="grid-item-bar">
                <h3>{article.title}</h3>
                <p>by: {article.source_id}</p>
                <a href={article.link} className="btn" target="_blank" rel="noopener noreferrer">
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    axios
      .get(`${URL.topHeadlinesURL}${URL.apiKey}${URL.country}us`)
      .then((response) => {
        const { results } = response.data;
        this.setState({
          articles: results,
        });
      })
      .catch((error) => {
        console.error('Error fetching headlines:', error);
      });
  }
}

export default Headlines;
