import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';
import './combined-styles.css'; // Corrected the path to the combined CSS file

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      source: props.source,
    };
  }

  render() {
    const { articles } = this.state;
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.gridContainer}>
          {/* Featured Headline */}
          {articles[0] && (
            <div className={`${classes.gridItem} ${classes.featuredItem}`}>
              <img src={articles[0].urlToImage} alt={articles[0].title} className={classes.image} />
              <div className={classes.content}>
                <h2>{articles[0].title}</h2>
                <p>Source: {articles[0].source.name}</p>
                <a
                  href={articles[0].url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn"
                >
                  Read More
                </a>
              </div>
            </div>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1).map((article) => (
            <div className={classes.gridItem} key={article.urlToImage}>
              <img src={article.urlToImage} alt={article.title} className={classes.image} />
              <div className={classes.content}>
                <h3>{article.title}</h3>
                <p>Source: {article.source.name}</p>
                <a
                  href={article.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn"
                >
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.reloadSource();
  }

  reloadSource() {
    axios
      .get(URL.topHeadlinesURL + URL.apiKey + URL.sources + this.props.source)
      .then((response) => {
        const { data } = response;
        this.setState({
          articles: data.articles,
        });
      })
      .catch((error) => {
        console.error('Error fetching news:', error);
      });
  }
}

const styles = {
  root: {
    padding: '20px',
    backgroundColor: '#f5f5f5', // Fixed background color
  },
  gridContainer: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
    gap: '20px',
  },
  gridItem: {
    display: 'flex',
    flexDirection: 'column',
    backgroundColor: '#fff',
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
    '&:hover': {
      transform: 'translateY(-5px)',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
    },
  },
  featuredItem: {
    gridColumn: 'span 3',
    gridRow: 'span 1',
  },
  image: {
    width: '100%',
    height: '200px',
    objectFit: 'cover',
  },
  content: {
    padding: '15px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    flexGrow: 1,
  },
};

News.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(News);
