import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      source: props.source,
    };
  }

  render() {
    let { articles, source } = this.state;

    if (source !== this.props.source) this.reloadSource();
    return (
      <div className="container">
        <h2>Berita dari {this.props.source}</h2>
        <div className="grid-list">
          {/* Featured Headline */}
          {articles[0] && (
            <div className="grid-item featured">
              <img src={articles[0].urlToImage} alt={articles[0].title} />
              <div className="grid-item-bar">
                <h2>{articles[0].title}</h2>
                <p>by: {articles[0].source.name}</p>
                <a href={articles[0].url} className="btn">Read More</a>
              </div>
            </div>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1).map((article) => (
            <div className="grid-item" key={article.urlToImage}>
              <img src={article.urlToImage} alt={article.title} />
              <div className="grid-item-bar">
                <h3>{article.title}</h3>
                <p>by: {article.source.name}</p>
                <a href={article.url} className="btn">Read More</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.reloadSource();
  }

  reloadSource() {
    axios
      .get(URL.topHeadlinesURL + URL.apiKey + URL.sources + this.props.source)
      .then((response) => {
        let { data } = response;
        this.setState({
          articles: data.articles,
        });
      });
  }
}

export default News;
