import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

class Headlines extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
    };
  }

  render() {
    const { articles } = this.state;
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <GridList cellHeight={200} className={classes.gridList} cols={4} spacing={20}>
          {/* Featured Headline */}
          {articles[0] && (
            <GridListTile key={articles[0].urlToImage} cols={2} rows={2} className={classes.featuredTile}>
              <img src={articles[0].urlToImage} alt={articles[0].title} className={classes.image} />
              <GridListTileBar
                title={articles[0].title}
                subtitle={<span>by: {articles[0].source.name}</span>}
                actionIcon={
                  <IconButton
                    className={classes.icon}
                    href={articles[0].url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <InfoIcon />
                  </IconButton>
                }
              />
            </GridListTile>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1).map((article) => (
            <GridListTile key={article.urlToImage} cols={1} rows={1} className={classes.tile}>
              <img src={article.urlToImage} alt={article.title} className={classes.image} />
              <GridListTileBar
                title={article.title}
                subtitle={<span>by: {article.source.name}</span>}
                actionIcon={
                  <IconButton
                    className={classes.icon}
                    href={article.url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <InfoIcon />
                  </IconButton>
                }
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }

  componentDidMount() {
    axios
      .get(URL.topHeadlinesURL + URL.countryID + URL.apiKey)
      .then((response) => {
        const { data } = response;
        this.setState({
          articles: data.articles,
        });
      })
      .catch((error) => {
        console.error('Error fetching headlines:', error);
      });
  }
}

const styles = (theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
    padding: '20px',
  },
  gridList: {
    width: '100%',
    maxWidth: 1200,
    height: 'auto',
  },
  featuredTile: {
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
  },
  tile: {
    borderRadius: '8px',
    overflow: 'hidden',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out',
    '&:hover': {
      transform: 'translateY(-5px)',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.2)',
    },
  },
  image: {
    width: '100%',
    height: '100%',
    objectFit: 'cover',
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.8)',
  },
});

Headlines.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Headlines);
