import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

class SearchNews extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      search: props.search,
    };
  }

  render() {
    let { articles, search } = this.state;

    if (search !== this.props.search) this.reloadSearch();
    return (
      <div className="container">
        <h2>Searching results for: {this.props.search}</h2>
        <div className="grid-list">
          {articles.map((article) => (
            <div className="grid-item" key={article.urlToImage}>
              <img src={article.urlToImage} alt={article.title} />
              <div className="grid-item-bar">
                <h3>{article.title}</h3>
                <p>by: {article.source.name}</p>
                <a href={article.url} className="btn">Read More</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    this.reloadSearch();
  }

  reloadSearch() {
    axios
      .get(URL.everythingURL + URL.apiKey + URL.search + this.props.search)
      .then((response) => {
        let { data } = response;
        this.setState({
          articles: data.articles,
        });
      });
  }
}

export default SearchNews;
