import React, { Component } from 'react'
import axios from 'axios'
import URL from './config'
import './combined-styles.css'; // Corrected the path to the combined CSS file

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';

class News extends Component {
  constructor(props) {
    super(props)
    this.state = {
      articles: [],
      source: props.source
    }
  }

  render() {
    let { articles, source } = this.state
    const { classes } = this.props

    if (source != this.props.source) this.reloadSource()
    return (
      <div className={classes.root}>
        <div className={classes.headlineContainer}>
          {/* Featured Headline */}
          {articles[0] && (
            <div className={classes.featuredHeadline}>
              <img src={articles[0].urlToImage} alt={articles[0].title} className={classes.featuredImage} />
              <div className={classes.featuredContent}>
                <h2>{articles[0].title}</h2>
                <p>by: {articles[0].source.name}</p>
                <a
                  href={articles[0].url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn"
                >
                  Read More
                </a>
              </div>
            </div>
          )}
        </div>

        <div className={classes.smallerHeadlines}>
          {/* Smaller Headlines */}
          {articles.slice(1).map((article) => (
            <div key={article.urlToImage} className={classes.smallHeadline}>
              <img src={article.urlToImage} alt={article.title} className={classes.smallImage} />
              <div className={classes.smallContent}>
                <h4>{article.title}</h4>
                <p>by: {article.source.name}</p>
                <a
                  href={article.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn"
                >
                  Read More
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }
  componentDidMount() {
    this.reloadSource()
  }

  reloadSource() {
    axios
      .get(URL.topHeadlinesURL + URL.apiKey + URL.sources + this.props.source)
      .then(response => {
        console.log(response.data)
        let { data } = response
        this.setState({
          articles: data.articles
        })
      })
  }
}
const styles = theme => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  headlineContainer: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginBottom: theme.spacing(4),
  },
  featuredHeadline: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '100%',
    maxWidth: 1200,
    marginBottom: theme.spacing(2),
  },
  featuredImage: {
    width: '50%',
    height: 'auto',
    marginRight: theme.spacing(2),
  },
  featuredContent: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
  },
  smallerHeadlines: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
    gap: theme.spacing(2),
  },
  smallHeadline: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    width: 300,
  },
  smallImage: {
    width: '100%',
    height: 'auto',
    marginBottom: theme.spacing(1),
  },
  smallContent: {
    textAlign: 'center',
  },
});

News.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(News);
