import React, { Component } from 'react'
import axios from 'axios'
import URL from './config'

import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';
import ListSubheader from '@material-ui/core/ListSubheader';
import IconButton from '@material-ui/core/IconButton';
import InfoIcon from '@material-ui/icons/Info';

class Headlines extends Component {
  constructor(props) {
    super(props)
    this.state = {
      articles: []
    }
  }

  render() {
    let { articles } = this.state;
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <GridList cellHeight={180} className={classes.gridList} cols={4}>
          {/* Featured Headline */}
          {articles[0] && (
            <GridListTile key={articles[0].urlToImage} cols={3} rows={2}>
              <img src={articles[0].urlToImage} alt={articles[0].title} />
              <GridListTileBar
                title={articles[0].title}
                subtitle={<span>by: {articles[0].source.name}</span>}
                actionIcon={
                  <IconButton className={classes.icon} href={articles[0].url}>
                    <InfoIcon />
                  </IconButton>
                }
              />
            </GridListTile>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1, 5).map((article) => (
            <GridListTile key={article.urlToImage} cols={1} rows={1}>
              <img src={article.urlToImage} alt={article.title} />
              <GridListTileBar
                title={article.title}
                subtitle={<span>by: {article.source.name}</span>}
                actionIcon={
                  <IconButton className={classes.icon} href={article.url}>
                    <InfoIcon />
                  </IconButton>
                }
              />
            </GridListTile>
          ))}
        </GridList>
      </div>
    );
  }

  componentDidMount() {
    axios
      .get(URL.topHeadlinesURL + URL.countryID + URL.apiKey)
      .then(response => {
        let { data } = response
        this.setState({
          articles: data.articles
        })
      })
  }
}
const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 1100,
    height: 900,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
});

Headlines.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Headlines);
