import React, { Component } from 'react';
import axios from 'axios';
import URL from './config';

class Headlines extends Component {
  constructor(props) {
    super(props);
    this.state = {
      articles: [],
    };
  }

  render() {
    let { articles } = this.state;

    return (
      <div className="container">
        <div className="grid-list">
          {/* Featured Headline */}
          {articles[0] && (
            <div className="grid-item featured">
              <img src={articles[0].urlToImage} alt={articles[0].title} />
              <div className="grid-item-bar">
                <h2>{articles[0].title}</h2>
                <p>by: {articles[0].source.name}</p>
                <a href={articles[0].url} className="btn">Read More</a>
              </div>
            </div>
          )}

          {/* Smaller Headlines */}
          {articles.slice(1, 5).map((article) => (
            <div className="grid-item" key={article.urlToImage}>
              <img src={article.urlToImage} alt={article.title} />
              <div className="grid-item-bar">
                <h3>{article.title}</h3>
                <p>by: {article.source.name}</p>
                <a href={article.url} className="btn">Read More</a>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  componentDidMount() {
    axios
      .get(URL.topHeadlinesURL + URL.countryID + URL.apiKey)
      .then((response) => {
        let { data } = response;
        this.setState({
          articles: data.articles,
        });
      });
  }
}

export default Headlines;
